(ns clojurians-log.styles
  (:require [garden-watcher.def :refer [defstyles]]
            [garden.color :as c]))

(def primary-color (c/rgb 62 49 60))

(def small-square-button
  {:display :inline-block
   :background-color primary-color
   :color :white
   :min-height "1rem"
   :min-width "1rem"
   :text-align :center
   :border-radius "20%"})

(def header-height "2rem")

(defstyles style
  [:html :body :div :span :applet :object :iframe :h1 :h2
   :h3 :h4 :h5 :h6 :p :blockquote :pre :a :abbr :acronym
   :address :big :cite :code :del :dfn :em :img :ins :kbd
   :q :s :samp :small :strike :strong :sub :sup :tt :var
   :b :u :i :center :dl :dt :dd :ol :ul :li :fieldset
   :form :label :legend :table :caption :tbody :tfoot
   :thead :tr :th :td :article :aside :canvas :details
   :embed :figure :figcaption :footer :header :hgroup
   :menu :nav :output :ruby :section :summary :time
   :mark :audio :video
   {:margin "0",
    :padding "0",
    :border "0",
    :font "inherit",
    :font-size "100%",
    :vertical-align "baseline"}]

  [:html {:line-height "1"}]

  [:ol :ul {:list-style "none"}]

  [:table {:border-collapse "collapse", :border-spacing "0"}]

  [:caption :th :td
   {:text-align "left", :font-weight "normal", :vertical-align "middle"}]

  [:q :blockquote {:quotes "none"}]

  ;; in original legacy.css:
  ;; q:before, q:after, blockquote:before, blockquote:after {
  ;; content: "";
  ;; content: none;
  ;; }

  [:q:before
   :q:after
   :blockquote:before
   :blockquote:after
   {:content "none"}]

  [:a [:img {:border "none"}]]

  [:article
   :aside
   :details
   :figcaption
   :figure
   :footer
   :header
   :hgroup
   :main
   :menu
   :nav
   :section
   :summary
   {:display "block"}]

  [:* {:box-sizing "border-box"}]

  [:html
   {:position "relative",
    :height "100%",
    :font-size "16px",
    :font-family "'Lato', sans-serif"}]

  [:body {:height "100%", :width "100%", :margin "0"}]

  [:.header {:height header-height, :width "100%"}]

  [:.main {:height "100%", :width "100%"}]

  [:.footer
   {:position "absolute",
    :left "0",
    :bottom "0",
    :height "64px",
    :width "100%"}]

  [:.channel-menu {:margin-top "1rem"}]

  [:.channel-menu_name
   {:display "inline-block",
    :padding "0 .5rem 0 2.5rem",
    :color "#555459",
    :font-size "1.4rem",
    :font-weight "900",
    :cursor "pointer"}]

  [:.channel-menu_prefix
   {:color "#9e9ea6", :padding-right ".1rem", :font-weight "500"}]

  [:.listings
   {:height "100%",
    :color "#ab9ba9",
    :background-color "#4d394b",
    :overflow-y "auto",
    :overflow-x "hidden"}]

  [:.message-history
   {:overflow-y "auto",
    :overflow-x "hidden",
    :height (format "calc(100%% - %s)" header-height),
    :margin-left "2rem"}]

  [:.listings_channels {:margin "1rem 0 2rem"}]

  [:.listings_header
   {:text-align "left",
    :font-size ".8rem",
    :line-height "1.25rem",
    :margin "0 1rem .1rem",
    :text-transform "uppercase",
    :font-weight "700",
    :color "#ab9ba9"}]

  [:.listings_header_date {:color "#4c9689"}]

  [:.channel_list
   {:list-style-type "none", :text-align "left", :color "#ab9ba9"}]

  [:.channel
   {:line-height "24px",
    :-moz-border-radius-topright "0.25rem",
    :-webkit-border-top-right-radius "0.25rem",
    :border-top-right-radius "0.25rem",
    :-moz-border-radius-bottomright "0.25rem",
    :-webkit-border-bottom-right-radius "0.25rem",
    :border-bottom-right-radius "0.25rem",
    :margin-right "17px",
    :color "#ffffff",
    :padding-left "1rem"}]

  [:.channel [:a {:color "#ffffff", :text-decoration "none"}]]

  [:.channel [:a:hover  {:text-decoration "underline"}]]

  [:.unread
   {:line-height "14px",
    :color "#ffffff",
    :vertical-align "baseline",
    :white-space "nowrap",
    :font-size ".8rem",
    :font-weight "700",
    :float "right",
    :margin-top "3px",
    :background "#eb4d5c",
    :padding "2px 9px",
    :text-shadow "0 1px 0 rgba(0,0,0,0.2)",
    :margin-right "3px",
    :border-radius "9px"}]

  [:.channel.active {:background "#4c9689"}]

  [:.channel_prefix {:color "#b2d5c9"}]

  [:.disclaimer
   {:font-size "0.8rem",
    :padding-left "1rem",
    :padding-top "1rem",
    :margin-right "17px"}]

  [:.message
   {:position "relative",
    :margin-top ".5rem",
    :padding ".25rem 2rem .1rem 3rem",
    :min-height "36px"}]

  [:.message_profile-pic
   {:width "36px",
    :-moz-border-radius "0.2rem",
    :display "block",
    :position "absolute",
    :-webkit-border-radius "0.2rem",
    :border-radius "0.2rem",
    :background-size "cover",
    :height "36px",
    :left "0",
    :background-image
    "url(data:image/png;base64,)"}]

  [:.message_username
   {:padding-right ".25rem",
    :color "#8b898f",
    :margin-left "0",
    :font-style "normal",
    :text-decoration "none"}]

  [:.message_timestamp
   {:line-height "1.2rem",
    :color "#babbbf",
    :text-align "left",
    :font-size "12px",
    :top "0",
    :margin-left "0",
    :width "36px",
    :display "inline",
    :position "relative",
    :margin-right "0",
    :left "0"}]

  [:.message_timestamp [:a {:text-decoration "none", :color "#babbbf"}]]

  [:.message_timestamp
   [:a:hover
    {:text-decoration "underline", :color "#5050df"}]]

  [:.message:target :.message.targeted {:background-color "#FFF8DC"}]

  [:.message_content {:display "block", :min-height "1rem"}]

  ;;in orginial legacy.css
  ;;  .user-menu {
  ;; float: left;
  ;; width: 220px;
  ;; height: 100%;
  ;; cursor: pointer;
  ;; background: #3e313c;
  ;; border-top: 2px solid #372c36;
  ;; padding: 7px 0 9px 8px;
  ;; height: 4rem;
  ;; position: fixed;
  ;; bottom: 0;
  ;; left: 0;
  ;; }

  [:.user-menu
   {:bottom "0",
    :float "left",
    :width "220px",
    :background "#3e313c",
    :cursor "pointer",
    :padding "7px 0 9px 8px",
    :position "fixed",
    :border-top "2px solid #372c36",
    :height "4rem",
    :left "0"}]

  [:.user-menu_profile-pic
   {:float "left",
    :width "48px",
    :background-image
    "url(data:image/png;base64,)",
    :-moz-border-radius "0.2rem",
    :display "inline-block",
    :-webkit-border-radius "0.2rem",
    :margin-right "8px",
    :border-radius "0.2rem",
    :background-size "cover",
    :height "48px"}]

  [:.user-menu_username
   {:display "block",
    :color "#ffffff",
    :font-weight "900",
    :line-height "1.5rem",
    :margin-top ".2rem",
    :max-width "120px"}]

  [:.connection_icon {:width "12px", :height "12px"}]

  [:.connection_status {:color "#ab9ba9"}]

  [:.input-box {:height "100%", :margin-left "220px"}]

  [:.input-box_text
   {:line-height "1.2rem",
    :box-shadow "none",
    :color "#3d3c40",
    :min-height "41px",
    :bottom "0",
    :font-size ".95rem",
    :margin-left "2%",
    :width "90%",
    :-webkit-background-clip "padding-box",
    :-moz-box-shadow "none",
    :background-clip "padding-box",
    :-moz-border-radius "0.2rem",
    :-webkit-box-shadow "none",
    :-webkit-appearance "none",
    :padding "9px 5px 9px 8px",
    :-moz-background-clip "padding-box",
    :outline "0",
    :-webkit-border-radius "0.2rem",
    :border "2px solid #e0e0e0",
    :border-radius "0.2rem",
    :margin-bottom "auto"}]

  [:pre.highlight
   {:margin ".5rem 0 .2rem", :font-size ".75rem", :line-height "1.15rem"}]

  [:pre
   {:background "#fbfaf8",
    :padding ".5rem",
    :word-break "normal",
    :display "block",
    :border "1px solid rgba(0, 0, 0, .15)",
    :white-space "pre-wrap",
    :word-wrap "break-word",
    :border-radius "4px"}]

  [:pre
   :code
   {:font-family "Monaco, Menlo, Consolas, \"Courier New\", monospace",
    :color "#333"}]

  [:code
   {:line-height "1.2",
    :white-space "normal",
    :color "#c25",
    :background-color "#f7f7f9",
    :border "1px solid #e1e1e8"}]

  [:pre.highlight
   [">"
    [:code
     {:white-space "pre-wrap",
      :background-color "transparent",
      :border "none",
      :color "inherit"}]]]

  [:h1 {:text-decoration "underline"}]

  [:.day-arrows
   {:margin "1rem 0 0 1rem"}

   [:div.day-prev (assoc small-square-button
                         :margin-right "0.3rem")]
   [:div.day-next (assoc small-square-button
                         :margin-left "0.3rem")]]

  [:.message.thread-msg {:margin-left "1rem"}]

  [:.emoji
   [:img {:height "22px"
          :width  "22px"}]]

  [:.content
   {:display "flex"
    :height "100%"
    :overflow "hidden"}]

  [:.sidebar
   {:max-width "250px"
    :flex-shrink "0"}]

  [:.channel-page
   [:.main
    {:margin "2rem"}]]

  [:.channel-list-page
   [:.main
    {:margin "2rem"}]]

  [:.about-page
   [:.main
    {:margin "2rem"}]]

  [:.sitemap-page
   [:.main
    {:margin "2rem"}]]

  [:.channel-index
   :.channel-days
   [:li
    {:margin "0.3rem 0"
     :font-size "1.2rem"}]]

  [:.app-title
   {:line-height "2rem"
    :font-weight 900
    :text-decoration "none"}]

  [:.sidebar
   [:.app-title
    {:padding "1rem 1rem"}]]

  [:.sidebar
   [:.app-title
    ["" :a
     {:color "#ffffff"
      :background "#3e313c";
      :border-bottom "2px solid #372c36"}]]]

  [:.app-title
   [:a:hover
    {:text-decoration "underline"}]]

  [:.padding-15px
   {:padding-left "15px"}]

  )
